import React from 'react';

export default class State extends React.Component{
    constructor(){
        super()
        this.state = {
            message: 'Do you think I will give up?'
        }
    }
    nope(){
        this.setState({
            message: 'Naaah bruh'
        })
    }
    render(){
        return(
            <div>
            <h1>Hello world!</h1>
            <p>{this.state.message}</p>
            <button onClick={() => nope()}></button>
            </div>
        )
    }
}
